<map version="1.0.0"><node ID="FFA94E3E-312B-4371-844E-BC0A7378D8B4" BACKGROUND_COLOR="#FFFFFF" TEXT="Patterns, Signals, and User Experience" COLOR="#415229" POSITION="right" STYLE="bubble"><edge COLOR="#ADD424" WIDTH="2" /><font NAME="HelveticaNeue" SIZE="18" /><node ID="CC157DE7-261B-4E07-957D-78F1EA0603F3" TEXT="KEY TAKEAWAYS" COLOR="#36342E" POSITION="right" STYLE="fork"><edge COLOR="#FEE221" WIDTH="6" /><font NAME="HelveticaNeue" SIZE="21" /><node ID="2ACEEDB7-C80F-4231-83D6-BB232B76264E" TEXT="You are working with PEOPLE" COLOR="#36342E" POSITION="right" STYLE="fork"><edge COLOR="#FEE221" WIDTH="5" /><font NAME="HelveticaNeue" SIZE="21" /><node ID="13CDC19B-019E-483A-B1F9-07BAE0DBF6EA" TEXT="you are not working with machines" COLOR="#36342E" POSITION="right" STYLE="fork"><edge COLOR="#FEE221" WIDTH="4" /><font NAME="HelveticaNeue" SIZE="21" /></node>
