<map version="1.0.0"><node ID="D9F10633-C0C2-494C-86C5-21A9FFDAB34D" BACKGROUND_COLOR="#FFFFFF" TEXT="FileMaker Under the Hood" COLOR="#526337" POSITION="right" STYLE="bubble"><edge COLOR="#ADD424" WIDTH="2" /><font NAME="HelveticaNeue-Medium" SIZE="18" /><node ID="5A95D168-7D3A-43A6-9D8D-3926716F61A9" TEXT="If you look at FileMaker binaries, they change every time you open the file." COLOR="#46443C" POSITION="right" STYLE="fork"><edge COLOR="#3CA43B" WIDTH="6" /><font NAME="HelveticaNeue-Light" SIZE="21" /></node>
<node ID="1E517880-6FF2-441A-A7B5-5C9CF5948E40" TEXT="FileMaker files are segmented into 4KB blocks" COLOR="#46443C" POSITION="right" STYLE="fork"><edge COLOR="#2FAEA1" WIDTH="6" /><font NAME="HelveticaNeue-Light" SIZE="21" /><node ID="1F32E267-308A-4338-AB48-18723A369A11" TEXT="so you don&#8217;t have to shift megabytes or terabytes when inserting into the &#8220;middle&#8221; of a file" COLOR="#46443C" POSITION="right" STYLE="fork"><edge COLOR="#2FAEA1" WIDTH="5" /><font NAME="HelveticaNeue-Light" SIZE="21" /></node>
<node ID="9A94847A-5585-4EB4-9366-383105D219D8" TEXT="they contain the data of" COLOR="#46443C" POSITION="right" STYLE="fork"><edge COLOR="#2FAEA1" WIDTH="5" /><font NAME="HelveticaNeue-Light" SIZE="21" /><node ID="657AB961-C5C9-444A-89C8-75A16756C25C" TEXT="layouts" COLOR="#46443C" POSITION="right" STYLE="fork"><edge COLOR="#2FAEA1" WIDTH="4" /><font NAME="HelveticaNeue-Light" SIZE="21" /></node>
<node ID="8EEE2A3F-F33E-4AE2-A391-AC19D4906316" TEXT="scripts" COLOR="#46443C" POSITION="right" STYLE="fork"><edge COLOR="#2FAEA1" WIDTH="4" /><font NAME="HelveticaNeue-Light" SIZE="21" /></node>
<node ID="D7118C34-15AD-4DD6-A640-08CA83B50E51" TEXT="table definitions" COLOR="#46443C" POSITION="right" STYLE="fork"><edge COLOR="#2FAEA1" WIDTH="4" /><font NAME="HelveticaNeue-Light" SIZE="21" /></node>
<node ID="B955609D-22F5-4869-9A13-8EFDC5B1F023" TEXT="record contents" COLOR="#46443C" POSITION="right" STYLE="fork"><edge COLOR="#2FAEA1" WIDTH="4" /><font NAME="HelveticaNeue-Light" SIZE="21" /></node>
</node>
<node ID="A16F28CC-85BD-4CCB-8F61-1A8DE6C91A0E" TEXT="blocks are linked together (forward &#38; backward pointers)" COLOR="#46443C" POSITION="right" STYLE="fork"><edge COLOR="#2FAEA1" WIDTH="5" /><font NAME="HelveticaNeue-Light" SIZE="21" /></node>
<node ID="09E701CC-400E-4553-90EF-615799DD7F00" TEXT="if you have a piece of data that is more than 4k, it gets split onto more than one block" COLOR="#46443C" POSITION="right" STYLE="fork"><edge COLOR="#2FAEA1" WIDTH="5" /><font NAME="HelveticaNeue-Light" SIZE="21" /></node>
<node ID="A2905D4A-1F08-430A-99CC-F788719D5241" TEXT="blocks fit where they fit." COLOR="#46443C" POSITION="right" STYLE="fork"><edge COLOR="#2FAEA1" WIDTH="5" /><font NAME="HelveticaNeue-Light" SIZE="21" /></node>
<node ID="BD50B4C3-CD95-4D91-BF37-904F128E4D57" TEXT="if you delete something, there&#8217;s an unused block in the middle of the file which can be reused (but might also sit there, empty but taking up disk space)" COLOR="#46443C" POSITION="right" STYLE="fork"><edge COLOR="#2FAEA1" WIDTH="5" /><font NAME="HelveticaNeue-Light" SIZE="21" /></node>
<node ID="0495C447-2D8D-4203-9F2E-C3F486E02A89" TEXT="blocks don&#8217;t have to be fully-filled" COLOR="#46443C" POSITION="right" STYLE="fork"><edge COLOR="#2FAEA1" WIDTH="5" /><font NAME="HelveticaNeue-Light" SIZE="21" /></node>
<node ID="123A905B-5AA1-4EF1-A72C-636402E617DC" TEXT="Slide: Contents of a block as text" COLOR="#46443C" POSITION="right" STYLE="fork"><edge COLOR="#2FAEA1" WIDTH="5" /><font NAME="HelveticaNeue-Light" SIZE="21" /><node ID="F5CC2DD2-2219-43F3-A597-F5E8D011A306" TEXT="(slides unavailable for download)" COLOR="#46443C" POSITION="right" STYLE="fork"><edge COLOR="#2FAEA1" WIDTH="4" /><font NAME="HelveticaNeue-Light" SIZE="21" /></node>
</node>
</node>
<node ID="8F7B7448-87B8-42A1-8803-127C07BCA621" TEXT="Every small piece of a file is addressed by an ID" COLOR="#46443C" POSITION="right" STYLE="fork"><edge COLOR="#2289AC" WIDTH="6" /><font NAME="HelveticaNeue-Light" SIZE="21" /><node ID="DB53A543-A2C9-49AC-876D-5ECE7877DFBD" TEXT="could be short, e.g. &#8220;10&#8221;" COLOR="#46443C" POSITION="right" STYLE="fork"><edge COLOR="#2289AC" WIDTH="5" /><font NAME="HelveticaNeue-Light" SIZE="21" /></node>
<node ID="A94F3B4D-58C7-4F41-9BE9-C22FA06D9C46" TEXT="or could be long, like &#8220;/10/01/01/1250120F143000000&#34;" COLOR="#46443C" POSITION="right" STYLE="fork"><edge COLOR="#2289AC" WIDTH="5" /><font NAME="HelveticaNeue-Light" SIZE="21" /></node>
<node ID="E84280BB-18C2-4300-BC52-334F66A997F2" TEXT="IDs are sorted sequentially" COLOR="#46443C" POSITION="right" STYLE="fork"><edge COLOR="#2289AC" WIDTH="5" /><font NAME="HelveticaNeue-Light" SIZE="21" /><node ID="7212F818-A074-4161-AA93-DFD32EE98ECC" TEXT="when FM is looking for something, it does a quick search per-ID" COLOR="#46443C" POSITION="right" STYLE="fork"><edge COLOR="#2289AC" WIDTH="4" /><font NAME="HelveticaNeue-Light" SIZE="21" /></node>
<node ID="2172FBA8-09E9-4533-A764-5DA349BF6EB1" TEXT="IDs can be used to infer groups/types (e.g., /10 = custom functions)" COLOR="#46443C" POSITION="right" STYLE="fork"><edge COLOR="#2289AC" WIDTH="4" /><font NAME="HelveticaNeue-Light" SIZE="21" /></node>
</node>
</node>
<node ID="BC7801B1-E0E8-4B8F-B757-45EA5970A87E" TEXT="Q &#38; A" COLOR="#526337" POSITION="right" STYLE="fork"><edge COLOR="#ADD424" WIDTH="6" /><font NAME="HelveticaNeue-Medium" SIZE="18" /><node ID="554F6FC2-D396-4EE7-AF07-1215766877F9" TEXT="Is it safe to use a recovered file?" COLOR="#46443C" POSITION="right" STYLE="fork"><edge COLOR="#E7514C" WIDTH="5" /><font NAME="HelveticaNeue-Light" SIZE="21" /><node ID="EEFE549C-E9F4-41B0-ACF8-893267001B1C" TEXT="Yes, if no data was lost." COLOR="#46443C" POSITION="right" STYLE="fork"><edge COLOR="#E7514C" WIDTH="4" /><font NAME="HelveticaNeue-Light" SIZE="21" /><node ID="E1207CB3-4E9C-459F-8A35-21E069DF207D" TEXT="Maybe can use FMDiff to tell?" COLOR="#46443C" POSITION="right" STYLE="fork"><edge COLOR="#E7514C" WIDTH="3" /><font NAME="HelveticaNeue-Light" SIZE="21" /></node>
</node>
<node ID="3C14229F-1306-48E7-B34F-2608D4D86726" TEXT="If data gets lost (part of block cleared, or block dropped) then you don&#8217;t know what is missing." COLOR="#46443C" POSITION="right" STYLE="fork"><edge COLOR="#E7514C" WIDTH="4" /><font NAME="HelveticaNeue-Light" SIZE="21" /></node>
</node>
<node ID="08970C7E-AFAD-4BCE-B8E3-2658D5F67294" TEXT="How does the consistency check work?" COLOR="#46443C" POSITION="right" STYLE="fork"><edge COLOR="#E7514C" WIDTH="5" /><font NAME="HelveticaNeue-Light" SIZE="21" /><node ID="2E85AB32-ADB7-4874-8CE3-6D11F63A4072" TEXT="It looks at whether the record is present or not." COLOR="#46443C" POSITION="right" STYLE="fork"><edge COLOR="#E7514C" WIDTH="4" /><font NAME="HelveticaNeue-Light" SIZE="21" /></node>
<node ID="46739B28-7ED6-4BB1-9557-B9DE0B239421" TEXT="It doesn&#8217;t differentiate between structural or data records." COLOR="#46443C" POSITION="right" STYLE="fork"><edge COLOR="#E7514C" WIDTH="4" /><font NAME="HelveticaNeue-Light" SIZE="21" /></node>
</node>
</node>
</node>
</map>
