<map version="1.0.0"><node ID="201CAA63-DAA5-41D9-8F08-29A6915C0949" BACKGROUND_COLOR="#FFFFFF" TEXT="FM &#38; Javascript" COLOR="#526337" POSITION="right" STYLE="bubble"><edge COLOR="#ADD424" WIDTH="2" /><font NAME="HelveticaNeue-Medium" SIZE="18" /><node ID="8A09E310-8B2B-425C-9864-F92CEFAB039D" TEXT="KEY TAKEAWAYS" COLOR="#46443C" POSITION="right" STYLE="fork"><edge COLOR="#E89608" WIDTH="6" /><font NAME="HelveticaNeue-Light" SIZE="21" /><node ID="E8A9BB4E-3CCE-4FA3-A57F-5F7DBE67786D" TEXT="Data URI is no longer best practice. Instead, export files as a quasi-webapp to flat directories." COLOR="#46443C" POSITION="right" STYLE="fork"><edge COLOR="#E89608" WIDTH="5" /><font NAME="HelveticaNeue-Light" SIZE="21" /></node>
</node>
